// AddressPage.js
import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import ApiService from "../../service/ApiService";
import MapComponent from "../future features/MapComponent";
import '../../style/addressPage.css';

const AddressPage = () => {
    const [address, setAddress] = useState({
        street: '',
        city: '',
        state: '',
        zipCode: '',
        country: ''
    });

    const [error, setError] = useState(null);
    const [isAddressSaved, setIsAddressSaved] = useState(false);
    const navigate = useNavigate();
    const location = useLocation();

    useEffect(() => {
        if (location.pathname === '/edit-address') {
            fetchUserInfo();
        }
    }, [location.pathname]);

    const fetchUserInfo = async () => {
        try {
            const response = await ApiService.getLoggedInUserInfo();
            if (response.user.address) {
                setAddress(response.user.address);
            }
        } catch (error) {
            setError(error.response?.data?.message || error.message || "Unable to fetch user information");
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAddress((prevAddress) => ({
            ...prevAddress,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await ApiService.saveAddress(address);
            setIsAddressSaved(true); // Indicate that the address is saved
        } catch (error) {
            setError(error.response?.data?.message || error.message || "Failed to save/update address");
        }
    };

    const handlePayPalPayment = () => {
        window.paypal.Buttons({
            createOrder: (data, actions) => {
                return actions.order.create({
                    purchase_units: [{
                        amount: {
                            value: '1.00' // Set the amount
                        }
                    }]
                });
            },
            onApprove: async (data, actions) => {
                await actions.order.capture();
                alert('Transaction completed by ' + data.payer.name.given_name);
                navigate("/cart"); // Redirect after payment
            },
            onError: (err) => {
                console.error('PayPal Checkout onError', err);
            }
        }).render('#paypal-button-container'); // Render the PayPal button
    };

    useEffect(() => {
        if (isAddressSaved) {
            handlePayPalPayment(); // Show PayPal button after address is saved
        }
    }, [isAddressSaved]);

    return (
        <div className="address-page">
            <h2>{location.pathname === '/edit-address' ? 'Edit Address' : "Add Address"}</h2>
            {error && <p className="error-message">{error}</p>}

            <form onSubmit={handleSubmit}>
                <label>
                    Street:
                    <input type="text"
                           name="street"
                           value={address.street}
                           onChange={handleChange}
                           required />
                </label>
                <label>
                    City:
                    <input type="text"
                           name="city"
                           value={address.city}
                           onChange={handleChange}
                           required />
                </label>
                <label>
                    State:
                    <input type="text"
                           name="state"
                           value={address.state}
                           onChange={handleChange}
                           required />
                </label>
                <label>
                    Zip Code:
                    <input type="text"
                           name="zipCode"
                           value={address.zipCode}
                           onChange={handleChange}
                           required />
                </label>
                <label>
                    Country:
                    <input type="text"
                           name="country"
                           value={address.country}
                           onChange={handleChange}
                           required />
                </label>
                <button type="submit">{location.pathname === '/edit-address' ? 'Edit Address' : "Save Address"}</button>
            </form>

            {/* Add the Map Component */}
            <MapComponent />

            {/* PayPal button container */}
            {isAddressSaved && <div id="paypal-button-container"></div>}
        </div>
    );
};

export default AddressPage;
