import React from "react";
import { useLocation } from "react-router-dom";
import '../../style/orderConfirmationPage.css';

const OrderConfirmationPage = () => {
    const location = useLocation();
    const { orderDetails } = location.state || { orderDetails: {} }; // Default to an empty object

    const { items, totalPrice, address } = orderDetails;

    return (
        <div className="order-confirmation-page">
            <h1>Order Confirmation</h1>
            <h2>Shipping Address</h2>
            <p>{address.street}, {address.city}, {address.state}, {address.zipCode}, {address.country}</p>
            <h2>Order Details</h2>
            <ul className="order-items">
                {items && items.length > 0 ? (
                    items.map(item => (
                        <li key={item.id} className="order-item">
                            <img src={item.imageUrl} alt={item.name} />
                            <div className="order-item-details">
                                <h4>{item.name}</h4>
                                <p>{item.description}</p>
                                <span>${item.price.toFixed(2)} x {item.quantity}</span>
                            </div>
                        </li>
                    ))
                ) : (
                    <li>No items in the order.</li>
                )}
            </ul>
            <h2>Total Amount: ${totalPrice.toFixed(2)}</h2>
        </div>
    );
};

export default OrderConfirmationPage;
