import axios from "axios";

export default class ApiService {
    static BASE_URL = "http://localhost:2424";

    static getHeader() {
        const token = localStorage.getItem("token");
        return {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json"
        };
    }

    /** AUTH & USERS API */
    static async registerUser(registration) {
        try {
            const response = await axios.post(`${this.BASE_URL}/auth/register`, registration);
            return response.data;
        } catch (error) {
            console.error("Error registering user:", error.response?.data || error.message);
            throw error;
        }
    }

    static async loginUser(loginDetails) {
        try {
            const response = await axios.post(`${this.BASE_URL}/auth/login`, loginDetails);
            return response.data;
        } catch (error) {
            console.error("Error logging in:", error.response?.data || error.message);
            throw error;
        }
    }

    static async getLoggedInUserInfo() {
        try {
            const response = await axios.get(`${this.BASE_URL}/user/my-info`, {
                headers: this.getHeader()
            });
            return response.data;
        } catch (error) {
            console.error("Error fetching logged-in user info:", error.response?.data || error.message);
            throw error;
        }
    }

    /** PRODUCT ENDPOINT */
    static async addProduct(formData) {
        try {
            const response = await axios.post(`${this.BASE_URL}/product/create`, formData, {
                headers: {
                    ...this.getHeader(),
                    "Content-Type": "multipart/form-data"
                }
            });
            return response.data;
        } catch (error) {
            console.error("Error adding product:", error.response?.data || error.message);
            throw error;
        }
    }

    static async getAllProducts() {
        try {
            const response = await axios.get(`${this.BASE_URL}/product/get-all`);
            return response.data;
        } catch (error) {
            console.error("Error fetching all products:", error.response?.data || error.message);
            throw error;
        }
    }

    // Add similar try-catch to other API calls for more clarity on errors.

    /** CART METHODS */
    static async getCartByUserId(userId) {
        try {
            const response = await axios.get(`${this.BASE_URL}/cart/user/${userId}`, {
                headers: this.getHeader()
            });
            return response.data;
        } catch (error) {
            console.error("Error fetching cart by user ID:", error.response?.data || error.message);
            throw error;
        }
    }

    static async addToCart(userId, productId, quantity) {
        try {
            const response = await axios.post(`${this.BASE_URL}/cart/add`, {
                userId,
                productId,
                quantity
            }, {
                headers: this.getHeader()
            });
            return response.data;
        } catch (error) {
            console.error("Error adding item to cart:", error.response?.data || error.message);
            throw error;
        }
    }

    static async updateCartItem(cartId, quantity) {
        try {
            const response = await axios.put(`${this.BASE_URL}/cart/update/${cartId}`, null, {
                headers: this.getHeader(),
                params: { quantity }
            });
            return response.data;
        } catch (error) {
            console.error("Error updating cart item:", error.response?.data || error.message);
            throw error;
        }
    }

    static async removeCartItem(cartId) {
        try {
            const response = await axios.delete(`${this.BASE_URL}/cart/remove/${cartId}`, {
                headers: this.getHeader()
            });
            return response.data;
        } catch (error) {
            console.error("Error removing cart item:", error.response?.data || error.message);
            throw error;
        }
    }
}
