import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import ApiService from "../../service/ApiService";
import MapComponent from '../../component/future features/MapComponent.jsx';
import '../../style/addressPage.css';

const AddressPage = () => {
    const [address, setAddress] = useState({
        street: '',
        city: '',
        state: '',
        zipCode: '',
        country: '',
        latitude: 0,  // Add latitude
        longitude: 0   // Add longitude
    });

    const [error, setError] = useState(null);
    const navigate = useNavigate();
    const location = useLocation();

    useEffect(() => {
        if (location.pathname === '/edit-address') {
            fetchUserInfo();
        }
    }, [location.pathname]);

    const fetchUserInfo = async () => {
        try {
            const response = await ApiService.getLoggedInUserInfo();
            if (response.user.address) {
                setAddress(response.user.address);
            }
        } catch (error) {
            setError(error.response?.data?.message || error.message || "Unable to fetch user information");
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAddress((prevAddress) => ({
            ...prevAddress,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await ApiService.saveAddress(address);
            navigate("/cart", { state: { addressSaved: true } });
        } catch (error) {
            setError(error.response?.data?.message || error.message || "Failed to save/update address");
        }
    };

    return (
        <div className="address-page">
            <h2>{location.pathname === '/edit-address' ? 'Edit Address' : "Add Address"}</h2>
            {error && <p className="error-message">{error}</p>}

            <form onSubmit={handleSubmit}>
                <label>
                    Street:
                    <input type="text"
                           name="street"
                           value={address.street}
                           onChange={handleChange}
                           required />
                </label>
                <label>
                    City:
                    <input type="text"
                           name="city"
                           value={address.city}
                           onChange={handleChange}
                           required />
                </label>
                <label>
                    State:
                    <input type="text"
                           name="state"
                           value={address.state}
                           onChange={handleChange}
                           required />
                </label>
                <label>
                    Zip Code:
                    <input type="text"
                           name="zipCode"
                           value={address.zipCode}
                           onChange={handleChange}
                           required />
                </label>
                <label>
                    Country:
                    <input type="text"
                           name="country"
                           value={address.country}
                           onChange={handleChange}
                           required />
                </label>
                <button type="submit">{location.pathname === '/edit-address' ? 'Edit Address' : "Save Address"}</button>
            </form>

            <MapComponent address={address} /> {/* Add the MapComponent here */}
        </div>
    );
};

export default AddressPage;
