import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import ApiService from "../../service/ApiService";
import { useCart } from "../context/CartContext";
import PayPalButton from '../../component/future features/PayPalButton'; // Import your PayPalButton
import '../../style/cartPage.css';

const CartPage = () => {
    const { cart, dispatch } = useCart();
    const [message, setMessage] = useState(null);
    const [paymentMethod, setPaymentMethod] = useState('cod'); // Default to Cash on Delivery
    const [address, setAddress] = useState(''); // State to store the address
    const navigate = useNavigate();
    const location = useLocation();

    const incrementItem = (product) => {
        dispatch({ type: 'INCREMENT_ITEM', payload: product });
    };

    const decrementItem = (product) => {
        const cartItem = cart.find(item => item.id === product.id);
        if (cartItem && cartItem.quantity > 1) {
            dispatch({ type: 'DECREMENT_ITEM', payload: product });
        } else {
            dispatch({ type: 'REMOVE_ITEM', payload: product });
        }
    };

    const totalPrice = cart.reduce((total, item) => total + item.price * item.quantity, 0);

    // Load address from local storage when component mounts
    useEffect(() => {
        const savedAddress = localStorage.getItem("address");
        if (savedAddress) {
            setAddress(savedAddress); // Set address state from local storage
        }
    }, []);

    const handleAddAddress = (newAddress) => {
        setAddress(newAddress); // Update the address state
        localStorage.setItem("address", newAddress); // Save to local storage
    };

    const handleCheckout = async () => {
        const confirmPlaceOrder = window.confirm("Do you want to place the order?");
        if (!confirmPlaceOrder) {
            return; // If the user cancels, exit the function
        }

        const orderItems = cart.map(item => ({
            productId: item.id,
            quantity: item.quantity
        }));

        const orderRequest = {
            totalPrice,
            items: orderItems,
            paymentMethod, // Use the selected payment method
            address // Include the address in the order request
        };

        try {
            const response = await ApiService.createOrder(orderRequest);
            setMessage(response.message);
            if (response.status === 200) {
                dispatch({ type: 'CLEAR_CART' });
                localStorage.removeItem("address"); // Clear address from local storage on successful order
                navigate("/order-confirmation"); // Navigate to an order confirmation page
            }
        } catch (error) {
            setMessage(error.response?.data?.message || error.message || 'Failed to place an order');
        }
    };

    return (
        <div className="cart-page">
            <h1>Cart</h1>
            {message && <p className="response-message">{message}</p>}

            {cart.length === 0 ? (
                <p>Your cart is empty</p>
            ) : (
                <div>
                    <ul>
                        {cart.map(item => (
                            <li key={item.id} className="cart-item">
                                <img src={item.imageUrl} alt={item.name} />
                                <div className="cart-item-details">
                                    <h2>{item.name}</h2>
                                    <p>{item.description}</p>
                                    <span>${item.price.toFixed(2)}</span>
                                </div>
                                <div className="quantity-controls">
                                    <button onClick={() => decrementItem(item)}>-</button>
                                    <span>{item.quantity}</span>
                                    <button onClick={() => incrementItem(item)}>+</button>
                                </div>
                            </li>
                        ))}
                    </ul>
                    <h2>Total: ${totalPrice.toFixed(2)}</h2>

                    {address ? (
                        <div className="payment-options">
                            <label>
                                <input
                                    type="radio"
                                    value="paypal"
                                    checked={paymentMethod === 'paypal'}
                                    onChange={() => setPaymentMethod('paypal')}
                                />
                                PayPal
                            </label>
                            <label>
                                <input
                                    type="radio"
                                    value="cod"
                                    checked={paymentMethod === 'cod'}
                                    onChange={() => setPaymentMethod('cod')}
                                />
                                Cash on Delivery
                            </label>

                            {/* Show PayPal button only if PayPal is selected */}
                            {paymentMethod === 'paypal' && <PayPalButton totalAmount={totalPrice} />}

                            {/* Place order button for Cash on Delivery only */}
                            {paymentMethod === 'cod' && (
                                <button className="checkout-button" onClick={handleCheckout}>
                                    Place Order
                                </button>
                            )}
                        </div>
                    ) : (
                        <button onClick={() => navigate('/add-address')} className="add-address-button">
                            Add Address
                        </button> // Button to add address
                    )}
                </div>
            )}
        </div>
    );
};

export default CartPage;
