// src/MessageParser.js
class MessageParser {
    constructor(props) {
        this.props = props;
    }

    parse(message) {
        const { actionProvider } = this.props;

        if (message.includes("hello")) {
            actionProvider.handleHello();
        } else if (message.includes("order status")) {
            actionProvider.handleOrderStatus();
        } else if (message.includes("product inquiry")) {
            actionProvider.handleProductInquiry();
        } else if (message.includes("admin help")) {
            actionProvider.handleAdminHelp();
        } else {
            actionProvider.handleUnknown();
        }
    }
}

export default MessageParser;
