import React, { useEffect } from 'react';
import '../../style/aboutUs.css';

const AboutUs = () => {
    // Scroll fade-in effect
    useEffect(() => {
        const handleScrollFade = () => {
            const elements = document.querySelectorAll('.fade-in');
            const triggerPoint = window.innerHeight / 1.1;

            elements.forEach((element) => {
                const elementTop = element.getBoundingClientRect().top;
                if (elementTop < triggerPoint) {
                    element.classList.add('visible');
                } else {
                    element.classList.remove('visible');
                }
            });
        };

        window.addEventListener('scroll', handleScrollFade);
        handleScrollFade(); // Initial check on load

        return () => {
            window.removeEventListener('scroll', handleScrollFade);
        };
    }, []);

    return (
        <div className="about-container">
            <section className="hero-section fade-in" id="hero">
                <h1>About Us</h1>
                <p>Welcome to SportsHub - your reliable destination for high-quality sports accessories and gear.</p>
            </section>

            <section className="our-story fade-in" id="story">
                <h2>Our Story</h2>
                <p>At SportsHub, we’re passionate about sports and believe everyone should have access to premium equipment. Our journey began with a simple idea: making top-notch sports accessories affordable and accessible to all. Today, we proudly serve athletes and sports enthusiasts around the world.</p>
            </section>

            <section className="values fade-in" id="values">
                <h2>Our Core Values</h2>
                <div className="value-list">
                    <div className="value-item">
                        <h3>Quality</h3>
                        <p>We maintain high standards in everything we offer, ensuring you receive only the best sports products.</p>
                    </div>
                    <div className="value-item">
                        <h3>Customer Focus</h3>
                        <p>Your satisfaction is our priority, and we work hard to provide outstanding service and support.</p>
                    </div>
                    <div className="value-item">
                        <h3>Innovation</h3>
                        <p>We constantly explore the latest trends and technologies to bring you the most advanced sports gear.</p>
                    </div>
                </div>
            </section>

            <section className="mission fade-in" id="mission">
                <h2>Our Mission</h2>
                <p>Our mission is to empower athletes of all levels by providing access to quality sports products. We strive to inspire a love for sports and support our customers in achieving their fitness and athletic goals.</p>
            </section>

            <section className="contact fade-in" id="contact">
                <h2>Get in Touch</h2>
                <p>If you have any questions or feedback, feel free to reach out. We’re here to help!</p>
                <a href="/contact" className="contact-button">Contact Us</a>
            </section>
        </div>
    );
};

export default AboutUs;
