import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import ApiService from "../../service/ApiService";
import { useCart } from "../context/CartContext";
import PayPalButton from '../../component/future features/PayPalButton';
import '../../style/cartPage.css';

const CartPage = () => {
    const { cart, dispatch } = useCart();
    const [message, setMessage] = useState(null);
    const [isAddressSaved, setIsAddressSaved] = useState(false); // State to track if address is saved
    const navigate = useNavigate();
    const location = useLocation();

    const incrementItem = (product) => {
        dispatch({ type: 'INCREMENT_ITEM', payload: product });
    };

    const decrementItem = (product) => {
        const cartItem = cart.find(item => item.id === product.id);
        if (cartItem && cartItem.quantity > 1) {
            dispatch({ type: 'DECREMENT_ITEM', payload: product });
        } else {
            dispatch({ type: 'REMOVE_ITEM', payload: product });
        }
    };

    const totalPrice = cart.reduce((total, item) => total + item.price * item.quantity, 0);

    useEffect(() => {
        if (location.state && location.state.addressSaved) {
            setIsAddressSaved(true); // Set address as saved
        }
    }, [location.state]);

    const handlePlaceOrder = async () => {
        // Your order handling logic here, e.g., calling an API
        const orderItems = cart.map(item => ({
            productId: item.id,
            quantity: item.quantity
        }));

        const orderRequest = {
            totalPrice,
            items: orderItems,
            paymentMethod: 'Cash on Delivery', // Change this based on the selected method
        };

        try {
            const response = await ApiService.createOrder(orderRequest);
            setMessage(response.message);
            dispatch({ type: 'CLEAR_CART' });
            navigate("/order-confirmation");
        } catch (error) {
            setMessage(error.response?.data?.message || error.message || 'Failed to place the order');
            setTimeout(() => {
                setMessage('');
            }, 3000);
        }
    };

    return (
        <div className="cart-page">
            <h1>Cart</h1>
            {message && <p className="response-message">{message}</p>}

            {cart.length === 0 ? (
                <p>Your cart is empty</p>
            ) : (
                <div>
                    <ul>
                        {cart.map(item => (
                            <li key={item.id} className="cart-item">
                                <img src={item.imageUrl} alt={item.name} />
                                <div className="cart-item-details">
                                    <h2>{item.name}</h2>
                                    <p>{item.description}</p>
                                    <span>${item.price.toFixed(2)}</span>
                                </div>
                                <div className="quantity-controls">
                                    <button onClick={() => decrementItem(item)}>-</button>
                                    <span>{item.quantity}</span>
                                    <button onClick={() => incrementItem(item)}>+</button>
                                </div>
                            </li>
                        ))}
                    </ul>
                    <h2>Total: ${totalPrice.toFixed(2)}</h2>

                    {isAddressSaved ? (
                        <div>
                            <PayPalButton totalAmount={totalPrice} />
                            <button className="checkout-button" onClick={handlePlaceOrder}>Place Order</button>
                        </div>
                    ) : (
                        <button onClick={() => navigate('/add-address')} className="add-address-button">
                            Proceed to Checkout
                        </button>
                    )}
                </div>
            )}
        </div>
    );
};

export default CartPage;
