import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import ApiService from "../../service/ApiService";
import { useCart } from "../context/CartContext";
import PayPalButton from '../../component/future features/PayPalButton'; // Import your PayPalButton
import '../../style/cartPage.css';

const CartPage = () => {
    const { cart, dispatch } = useCart();
    const [message, setMessage] = useState(null);
    const [paymentMethod, setPaymentMethod] = useState("paypal"); // Default to PayPal
    const [openPayPal, setOpenPayPal] = useState(false);
    const navigate = useNavigate();
    const location = useLocation();

    const incrementItem = (product) => {
        dispatch({ type: 'INCREMENT_ITEM', payload: product });
    };

    const decrementItem = (product) => {
        const cartItem = cart.find(item => item.id === product.id);
        if (cartItem && cartItem.quantity > 1) {
            dispatch({ type: 'DECREMENT_ITEM', payload: product });
        } else {
            dispatch({ type: 'REMOVE_ITEM', payload: product });
        }
    };

    const totalPrice = cart.reduce((total, item) => total + item.price * item.quantity, 0);

    useEffect(() => {
        if (location.state && location.state.addressSaved && paymentMethod === "paypal") {
            setOpenPayPal(true); // Automatically open PayPal if address is saved and PayPal is selected
        }
    }, [location.state, paymentMethod]);

    const handlePaymentSelection = (e) => {
        setPaymentMethod(e.target.value);
        setOpenPayPal(false); // Reset PayPal button if switching payment methods
    };

    const handleCOD = () => {
        // Handle Cash on Delivery logic here
        setMessage("Your order has been placed with Cash on Delivery.");
        // Navigate to a confirmation page or show order summary
        navigate("/order-confirmation");
    };

    return (
        <div className="cart-page">
            <h1>Cart</h1>
            {message && <p className="response-message">{message}</p>}

            {cart.length === 0 ? (
                <p>Your cart is empty</p>
            ) : (
                <div>
                    <ul>
                        {cart.map(item => (
                            <li key={item.id} className="cart-item">
                                <img src={item.imageUrl} alt={item.name} />
                                <div className="cart-item-details">
                                    <h2>{item.name}</h2>
                                    <p>{item.description}</p>
                                    <span>${item.price.toFixed(2)}</span>
                                </div>
                                <div className="quantity-controls">
                                    <button onClick={() => decrementItem(item)}>-</button>
                                    <span>{item.quantity}</span>
                                    <button onClick={() => incrementItem(item)}>+</button>
                                </div>
                            </li>
                        ))}
                    </ul>
                    <h2>Total: ${totalPrice.toFixed(2)}</h2>

                    {/* Payment Method Selection */}
                    <div className="payment-options">
                        <h3>Select Payment Method:</h3>
                        <label>
                            <input
                                type="radio"
                                value="paypal"
                                checked={paymentMethod === "paypal"}
                                onChange={handlePaymentSelection}
                            />
                            PayPal
                        </label>
                        <label>
                            <input
                                type="radio"
                                value="cod"
                                checked={paymentMethod === "cod"}
                                onChange={handlePaymentSelection}
                            />
                            Cash on Delivery
                        </label>
                    </div>

                    {/* Display PayPal or COD based on selection */}
                    {paymentMethod === "paypal" && openPayPal ? (
                        <PayPalButton totalAmount={totalPrice} /> // Show PayPal button if PayPal is selected
                    ) : paymentMethod === "cod" ? (
                        <button onClick={handleCOD}>Place Order with Cash on Delivery</button>
                    ) : null}
                </div>
            )}
        </div>
    );
};

export default CartPage;
