// src/MessageParser.js
class MessageParser {
    constructor(props) {
        this.props = props;
    }

    parse(message) {
        const { messageHandler } = this.props;

        // Simple handling logic
        if (message.includes("hello")) {
            messageHandler("Hello! How can I help you?");
        } else if (message.includes("order")) {
            messageHandler("To check your order status, please provide your order ID.");
        } else {
            messageHandler("I'm sorry, I didn't understand that.");
        }
    }
}

export default MessageParser;
