// src/ActionProvider.js
import { createChatBotMessage } from 'react-chatbot-kit';

class ActionProvider {
    constructor(createChatBotMessage, setStateFunc) {
        this.createChatBotMessage = createChatBotMessage;
        this.setState = setStateFunc;
    }

    handleHello = () => {
        const message = this.createChatBotMessage("Hello! How can I assist you today?");
        this.addMessageToChat(message);
    };

    handleOrderStatus = () => {
        const message = this.createChatBotMessage("To check your order status, please provide your order ID.");
        this.addMessageToChat(message);
    };

    handleProductInquiry = () => {
        const message = this.createChatBotMessage("Please tell me the product you are inquiring about.");
        this.addMessageToChat(message);
    };

    handleAdminHelp = () => {
        const message = this.createChatBotMessage("As an admin, you can manage users, products, and view analytics.");
        this.addMessageToChat(message);
    };

    handleUnknown = () => {
        const message = this.createChatBotMessage("I'm sorry, I didn't understand that. Can you rephrase?");
        this.addMessageToChat(message);
    };

    addMessageToChat = (message) => {
        this.setState((prev) => ({
            ...prev,
            messages: [...prev.messages, message],
        }));
    };
}

export default ActionProvider;
