import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../context/CartContext";
import PayPalButton from '../../component/future features/PayPalButton';
import '../../style/cart.css';

const CartPage = () => {
    const { cart, dispatch } = useCart();
    const [paymentMethod, setPaymentMethod] = useState(null);
    const totalPrice = cart.reduce((total, item) => total + item.price * item.quantity, 0);
    const navigate = useNavigate();

    const handleCheckout = () => {
        if (paymentMethod === "cash") {
            navigate("/add-address"); // Navigate to the Address Page for Cash on Delivery
        } else if (paymentMethod === "paypal") {
            // You can handle PayPal payment logic here
            console.log("Proceeding to PayPal");
            // Consider adding logic to show PayPal button or redirect to PayPal
        } else {
            alert("Please select a payment method."); // Alert if no payment method is selected
        }
    };

    return (
        <div className="cart-page">
            <h1>Cart</h1>

            {cart.length === 0 ? (
                <p>Your cart is empty</p>
            ) : (
                <div>
                    <ul>
                        {cart.map(item => (
                            <li key={item.id} className="cart-item">
                                <img src={item.imageUrl} alt={item.name} />
                                <div className="cart-item-details">
                                    <h2>{item.name}</h2>
                                    <p>{item.description}</p>
                                    <span>${item.price.toFixed(2)}</span>
                                </div>
                                <div className="quantity-controls">
                                    <button onClick={() => dispatch({ type: 'DECREMENT_ITEM', payload: item })}>-</button>
                                    <span>{item.quantity}</span>
                                    <button onClick={() => dispatch({ type: 'INCREMENT_ITEM', payload: item })}>+</button>
                                </div>
                            </li>
                        ))}
                    </ul>
                    <h2>Total: ${totalPrice.toFixed(2)}</h2>

                    <div className="payment-options">
                        <button onClick={() => setPaymentMethod("paypal")}>Pay with PayPal</button>
                        <button onClick={() => setPaymentMethod("cash")}>Cash on Delivery</button>
                    </div>

                    <button className="checkout-button" onClick={handleCheckout}>
                        {paymentMethod === "paypal" ? "Proceed to PayPal" : "Proceed to Add Address"}
                    </button>

                    {paymentMethod === "paypal" && <PayPalButton totalAmount={totalPrice} />}
                </div>
            )}
        </div>
    );
};

export default CartPage;
