// CartContext.js
import React, { createContext, useReducer, useContext, useEffect } from "react";

const CartContext = createContext();

const initialState = {
    cart: [],
    userId: null
};

const cartReducer = (state, action) => {
    switch (action.type) {
        case 'SET_USER': {
            // Load the cart based on the new user's ID from localStorage
            const userId = action.payload;
            const savedCart = JSON.parse(localStorage.getItem(`cart_${userId}`)) || [];
            return { ...state, cart: savedCart, userId };
        }
        case 'ADD_ITEM': {
            const existingItem = state.cart.find(item => item.id === action.payload.id);
            let newCart;

            if (existingItem) {
                newCart = state.cart.map(item =>
                    item.id === action.payload.id
                        ? { ...item, quantity: item.quantity + 1 }
                        : item
                );
            } else {
                newCart = [...state.cart, { ...action.payload, quantity: 1 }];
            }
            localStorage.setItem(`cart_${state.userId}`, JSON.stringify(newCart));
            return { ...state, cart: newCart };
        }
        case 'REMOVE_ITEM': {
            const newCart = state.cart.filter(item => item.id !== action.payload.id);
            localStorage.setItem(`cart_${state.userId}`, JSON.stringify(newCart));
            return { ...state, cart: newCart };
        }
        case 'INCREMENT_ITEM': {
            const newCart = state.cart.map(item =>
                item.id === action.payload.id
                    ? { ...item, quantity: item.quantity + 1 }
                    : item
            );
            localStorage.setItem(`cart_${state.userId}`, JSON.stringify(newCart));
            return { ...state, cart: newCart };
        }
        case 'DECREMENT_ITEM': {
            const newCart = state.cart.map(item =>
                item.id === action.payload.id && item.quantity > 1
                    ? { ...item, quantity: item.quantity - 1 }
                    : item
            );
            localStorage.setItem(`cart_${state.userId}`, JSON.stringify(newCart));
            return { ...state, cart: newCart };
        }
        case 'CLEAR_CART': {
            localStorage.removeItem(`cart_${state.userId}`);
            return { ...state, cart: [] };
        }
        default:
            return state;
    }
};

export const CartProvider = ({ children }) => {
    const [state, dispatch] = useReducer(cartReducer, initialState);

    useEffect(() => {
        if (state.userId) {
            localStorage.setItem(`cart_${state.userId}`, JSON.stringify(state.cart));
        }
    }, [state.cart, state.userId]);

    return (
        <CartContext.Provider value={{ cart: state.cart, dispatch, userId: state.userId }}>
            {children}
        </CartContext.Provider>
    );
};

export const useCart = () => useContext(CartContext);
