// src/MessageParser.js
class MessageParser {
    constructor(props) {
        this.props = props; // Ensure props are assigned correctly
    }

    parse(message) {
        const { actionProvider } = this.props; // Access actionProvider from props

        if (message.includes("hello")) {
            actionProvider.handleHello();
        } else if (message.includes("order")) {
            actionProvider.handleOrderStatus();
        } else {
            actionProvider.handleUnknown(); // This should work if actionProvider is correctly passed
        }
    }
}

export default MessageParser;
