import React from "react";
import { useLocation } from "react-router-dom";
import '../../style/orderConfirmation.css';

const OrderConfirmationPage = () => {
    const location = useLocation();
    const { orderDetails } = location.state || {}; // Get order details passed from CartPage

    if (!orderDetails) {
        return <div>No order details available.</div>;
    }

    return (
        <div className="order-confirmation-page">
            <h1>Order Confirmation</h1>
            <h2>Payment Method: {orderDetails.paymentMethod}</h2>
            <h2>Total Amount: ${orderDetails.totalPrice.toFixed(2)}</h2>
            <h3>Order Details:</h3>
            <ul>
                {orderDetails.items.map(item => (
                    <li key={item.id} className="order-item">
                        <img src={item.imageUrl} alt={item.name} />
                        <div className="order-item-details">
                            <h4>{item.name}</h4>
                            <p>{item.description}</p>
                            <span>${item.price.toFixed(2)} x {item.quantity}</span>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default OrderConfirmationPage;
