import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import ApiService from "../../service/ApiService";
import { useCart } from "../context/CartContext";
import PayPalButton from '../../component/future features/PayPalButton';
import '../../style/cart.css';

const CartPage = () => {
    const { cart, dispatch } = useCart();
    const [message, setMessage] = useState(null);
    const [openPayPal, setOpenPayPal] = useState(false);
    const [showCODConfirmation, setShowCODConfirmation] = useState(false);
    const navigate = useNavigate();
    const location = useLocation();

    const incrementItem = (product) => {
        dispatch({ type: 'INCREMENT_ITEM', payload: product });
    };

    const decrementItem = (product) => {
        const cartItem = cart.find(item => item.id === product.id);
        if (cartItem && cartItem.quantity > 1) {
            dispatch({ type: 'DECREMENT_ITEM', payload: product });
        } else {
            dispatch({ type: 'REMOVE_ITEM', payload: product });
        }
    };

    const totalPrice = cart.reduce((total, item) => total + item.price * item.quantity, 0);

    useEffect(() => {
        if (location.state && location.state.addressSaved) {
            setOpenPayPal(true); // Automatically open PayPal if the address was just saved
        }
    }, [location.state]);

    const handleCOD = () => {
        setShowCODConfirmation(true); // Show COD confirmation
        // Optionally, send a request to the server to mark the order as COD
        ApiService.placeOrder({ paymentMethod: "COD", totalAmount: totalPrice })
            .then(() => setMessage("Your order has been placed successfully with Cash on Delivery!"))
            .catch((error) => setMessage("Failed to place order: " + (error.message || error.response?.data?.message)));
    };

    return (
        <div className="cart-page">
            <h1>Cart</h1>
            {message && <p className="response-message">{message}</p>}

            {cart.length === 0 ? (
                <p>Your cart is empty</p>
            ) : (
                <div>
                    <ul>
                        {cart.map(item => (
                            <li key={item.id} className="cart-item">
                                <img src={item.imageUrl} alt={item.name} />
                                <div className="cart-item-details">
                                    <h2>{item.name}</h2>
                                    <p>{item.description}</p>
                                    <span>${item.price.toFixed(2)}</span>
                                </div>
                                <div className="quantity-controls">
                                    <button onClick={() => decrementItem(item)}>-</button>
                                    <span>{item.quantity}</span>
                                    <button onClick={() => incrementItem(item)}>+</button>
                                </div>
                            </li>
                        ))}
                    </ul>
                    <h2>Total: ${totalPrice.toFixed(2)}</h2>

                    {/* Display PayPal button if address was saved */}
                    {openPayPal ? (
                        <PayPalButton totalAmount={totalPrice} />
                    ) : (
                        <button onClick={() => navigate('/add-address')}>Add Address</button>
                    )}

                    {/* Cash on Delivery button */}
                    <button onClick={handleCOD} className="cod-button">Cash on Delivery</button>

                    {/* COD Confirmation message */}
                    {showCODConfirmation && <p>Your order has been placed with Cash on Delivery. Thank you!</p>}
                </div>
            )}
        </div>
    );
};

export default CartPage;
