import React from 'react';
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";

const PayPalButton = () => {
    const testAmount = "1.00"; // Amount set to 1 INR

    return (
        <PayPalScriptProvider options={{ "client-id": "AQnZkbeWlWUtMrejlLoVqUf8d1bIlK28dOsb2vKrtjQ0lEvX8C5g1OowLPbKonCUmF0W_0zp7SvFw5VN" }}>
            <PayPalButtons
                createOrder={(data, actions) => {
                    return actions.order.create({
                        purchase_units: [{
                            amount: {
                                currency_code: "INR", // Set currency to INR
                                value: "1.00", // Set the amount to 1 INR
                            },
                        }],
                    });
                }}
                onApprove={async (data, actions) => {
                    const order = await actions.order.capture();
                    alert('Transaction completed by ' + order.payer.name.given_name);
                    // Optionally, you can clear the cart or redirect after successful payment
                }}
                onError={(err) => {
                    console.error('PayPal Checkout onError', err);
                }}
            />
        </PayPalScriptProvider>
    );
};

export default PayPalButton;
