// AddressPage.js
import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import ApiService from "../../service/ApiService";
import MapComponent from '../future features/MapComponent'; // Adjust the path as needed
import '../../style/addressPage.css';
import axios from 'axios';

const AddressPage = () => {
    const [address, setAddress] = useState({
        street: '',
        city: '',
        state: '',
        zipCode: '',
        country: ''
    });

    const [coordinates, setCoordinates] = useState({ lat: null, lng: null });
    const [error, setError] = useState(null);
    const navigate = useNavigate();
    const location = useLocation();

    useEffect(() => {
        if (location.pathname === '/edit-address') {
            fetchUserInfo();
        }
    }, [location.pathname]);

    const fetchUserInfo = async () => {
        try {
            const response = await ApiService.getLoggedInUserInfo();
            if (response.user.address) {
                setAddress(response.user.address);
                // Get coordinates when editing address
                await fetchCoordinates(response.user.address);
            }
        } catch (error) {
            setError(error.response?.data?.message || error.message || "Unable to fetch user information");
        }
    };

    const fetchCoordinates = async (address) => {
        const { street, city, state, country } = address;
        const fullAddress = `${street}, ${city}, ${state}, ${country}`;

        try {
            const response = await axios.get(`https://api.opencagedata.com/geocode/v1/json`, {
                params: {
                    q: fullAddress,
                    key: '8f741c7ce35544609646455395ac00a9', // Your OpenCage API key
                    limit: 1,
                },
            });
            const { lat, lng } = response.data.results[0].geometry;
            setCoordinates({ lat, lng });
        } catch (error) {
            console.error("Error fetching coordinates:", error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAddress((prevAddress) => ({
            ...prevAddress,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await ApiService.saveAddress(address);
            await fetchCoordinates(address); // Fetch coordinates after saving
            navigate("/cart", { state: { addressSaved: true, coordinates } }); // Pass coordinates
        } catch (error) {
            setError(error.response?.data?.message || error.message || "Failed to save/update address");
        }
    };

    return (
        <div className="address-page">
            <h2>{location.pathname === '/edit-address' ? 'Edit Address' : "Add Address"}</h2>
            {error && <p className="error-message">{error}</p>}

            <form onSubmit={handleSubmit}>
                <label>
                    Street:
                    <input type="text"
                           name="street"
                           value={address.street}
                           onChange={handleChange}
                           required />
                </label>
                <label>
                    City:
                    <input type="text"
                           name="city"
                           value={address.city}
                           onChange={handleChange}
                           required />
                </label>
                <label>
                    State:
                    <input type="text"
                           name="state"
                           value={address.state}
                           onChange={handleChange}
                           required />
                </label>
                <label>
                    Zip Code:
                    <input type="text"
                           name="zipCode"
                           value={address.zipCode}
                           onChange={handleChange}
                           required />
                </label>
                <label>
                    Country:
                    <input type="text"
                           name="country"
                           value={address.country}
                           onChange={handleChange}
                           required />
                </label>
                <button type="submit">{location.pathname === '/edit-address' ? 'Edit Address' : "Save Address"}</button>
            </form>

            {coordinates.lat && coordinates.lng && (
                <MapComponent position={[coordinates.lat, coordinates.lng]} />
            )}
        </div>
    );
};

export default AddressPage;
