import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CartPage = () => {
    const [cartItems, setCartItems] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchCartItems = async () => {
        try {
            const response = await axios.get('/cart/user', {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('authToken')}`, // Using token for authentication
                },
            });
            setCartItems(response.data);
            setLoading(false);
        } catch (err) {
            setError('Failed to fetch cart items');
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchCartItems();
    }, []);

    const handleUpdateQuantity = async (cartId, quantity) => {
        try {
            await axios.put(`/cart/update/${cartId}`, { quantity }, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                },
            });
            fetchCartItems();
        } catch (err) {
            setError('Failed to update item quantity');
        }
    };

    const handleRemoveItem = async (cartId) => {
        try {
            await axios.delete(`/cart/remove/${cartId}`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                },
            });
            fetchCartItems();
        } catch (err) {
            setError('Failed to remove item from cart');
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div className="cart">
            <h1>Your Cart</h1>
            <ul>
                {cartItems.map(item => (
                    <li key={item.id}>
                        <div>
                            <img src={item.product.imageUrl} alt={item.product.name} />
                            <span>{item.product.name}</span>
                            <span>{item.product.price}</span>
                            <div>
                                <button onClick={() => handleUpdateQuantity(item.id, item.quantity - 1)}>-</button>
                                <span>{item.quantity}</span>
                                <button onClick={() => handleUpdateQuantity(item.id, item.quantity + 1)}>+</button>
                            </div>
                            <button onClick={() => handleRemoveItem(item.id)}>Remove</button>
                        </div>
                    </li>
                ))}
            </ul>
            <div>
                <h2>Total: {/* Calculate total here */}</h2>
                <button>Proceed to Checkout</button>
            </div>
        </div>
    );
};

export default CartPage;
